import React, { useState, useEffect } from 'react';
import ControleLivros from '../src/controle/ControleLivros';
import ControleEditora from './ControleEditora';

function LinhaLivro(props) {
  const { livro, excluir } = props;
  const nomeEditora = ControleEditora.getNomeEditora(livro.codEditora);

  const autores = livro.autores.map((autor, index) => (
    <li key={index}>{autor}</li>
  ));

  return (
    <tr>
      <td>
        <button onClick={() => excluir(livro.codigo)}>Excluir</button>
      </td>
      <td>{livro.titulo}</td>
      <td>{nomeEditora}</td>
      <td>{autores}</td>
      <td>{livro.ano}</td>
    </tr>
  );
}

export default function LivroLista() {
  const [livros, setLivros] = useState([]);
  const [carregado, setCarregado] = useState(false);

  useEffect(() => {
    async function obterLivros() {
      const listaLivros = await ControleLivros.obterLivros();
      setLivros(listaLivros);
      setCarregado(true);
    }
    if (!carregado) {
      obterLivros();
    }
  }, [carregado]);

  const excluir = (codLivro) => {
    ControleLivros.excluir(codLivro);
    setCarregado(false);
  };

  const listaLinhas = livros.map((livro) => (
    <LinhaLivro key={livro.codigo} livro={livro} excluir={excluir} />
  ));

  return (
    <main>
      <h1>Livros</h1>
      <table>
        <thead>
          <tr>
            <th>Ações</th>
            <th>Título</th>
            <th>Editora</th>
            <th>Autores</th>
            <th>Ano</th>
          </tr>
        </thead>
        <tbody>{listaLinhas}</tbody>
      </table>
    </main>
  );
}
